public function setOrderStatusColor() {
	$json = array();

	$this->load->model('localisation/order_status');
	/*ALTER TABLE `oc_order_status` ADD `bg_color` VARCHAR(80) NOT NULL AFTER `name`, ADD `text_color` VARCHAR(80) NOT NULL AFTER `bg_color`; */

	$columns_mapping = array('background_color' => 'bg_color', 'text_color' => 'text_color');

	if (!empty($this->request->post['order_status_id']) && !empty($this->request->post['action']) && isset($this->request->post['color']) && isset($columns_mapping[$this->request->post['action']])) {

		$this->model_localisation_order_status->setOrderStatusColor($this->request->post['order_status_id'], array('column' => $columns_mapping[$this->request->post['action']], 'value' => $this->request->post['color'] ));
	}

	$this->response->addHeader('Content-Type: application/json');
	$this->response->setOutput(json_encode($json));
}

